DROP DATABASE IF EXISTS collegeCraft;
CREATE DATABASE IF NOT EXISTS collegeCraft;
USE collegeCraft;
DROP TABLE IF EXISTS users, roles, product, comments, sells, reputation;
CREATE TABLE users (username varchar(15) NOT NULL UNIQUE, hashed_password varchar(255) NOT NULL, email_address varchar(30), user_id INT AUTO_INCREMENT, bio varchar(512), reputation_id INT, role_id INT, location varchar(128), name varchar(32), PRIMARY KEY (user_id));
CREATE TABLE roles (role_id INT, role varchar(20), PRIMARY KEY (role_id)); 
CREATE TABLE product (product_id INT, description varchar(256), price INT NOT NULL, category varchar(256), PRIMARY KEY (product_id));
CREATE TABLE reputation (reputation_id INT, trust_level INT, statistics varchar(256), PRIMARY KEY (reputation_id)); 
CREATE TABLE sells (user_id INT, product_id INT, PRIMARY KEY (user_id, product_id), FOREIGN KEY (user_id) REFERENCES users(user_id), FOREIGN KEY (product_id) REFERENCES product(product_id));
CREATE TABLE comments (comment_id INT, product_id INT, reviewer INT NOT NULL, review varchar(256), rating INT, PRIMARY KEY (comment_id), FOREIGN KEY (product_id) REFERENCES product(product_id), FOREIGN KEY (reviewer) REFERENCES users(user_id));
